mapper.select:
 select id, name, description, icon, pid, seq, status, sys_level, create_time
    from pcs_001.subsystem
where  id= #{id}

select id, name, description, icon, pid, seq, status, sys_level, create_time
    from pcs_001.subsystem
    where name=#{name};

select id, name, description, icon, pid, seq, status, sys_level, create_time
from pcs_001.subsystem
where sys_level=0;

select id, name, description, icon, pid, seq, status, sys_level, create_time
    from pcs_001.subsystem
    where pid=#{category_id}
